mapper.select:

select 

from vehicle_charge_drive_info
where vin = #{vin,jdbcType=VARCHAR}
  and data_type = #{dataType,jdbcType=INTEGER}
  and begin_time = #{beginTime,jdbcType=TIMESTAMP}
  and end_time = #{endTime,jdbcType=TIMESTAMP}

mapper.delete:

delete from vehicle_charge_drive_info
where vin = #{vin,jdbcType=VARCHAR}
  and data_type = #{dataType,jdbcType=INTEGER}
  and begin_time = #{beginTime,jdbcType=TIMESTAMP}
  and end_time = #{endTime,jdbcType=TIMESTAMP}

mapper.insert:

