  #{endTime,jdbcType=TIMESTAMP}, #{beginLatLon,jdbcType=VARCHAR}, #{endLatLon,jdbcType=VARCHAR}, 
  #{beginSoc,jdbcType=INTEGER}, #{endSoc,jdbcType=INTEGER}, #{distance,jdbcType=DOUBLE}
  )

mapper.update:

